import axios from 'axios';
import React from 'react'

const Login = () => {
    
  const registerUsername = React.createRef();
  const registerMail = React.createRef();
  const registerPassword = React.createRef();

  const loginMail = React.createRef();
  const loginPassword = React.createRef();

  const handleRegister = async() => {
    const registerCredentials = {
        username: registerUsername.current.value,
        email_id: registerMail.current.value,
        password: registerPassword.current.value
    }
    await axios.post("http://localhost:8000/register", registerCredentials)
    .then(res => {
      console.log(res.data);
      const jwt = res.data.token;
      localStorage.setItem("secretToken", jwt);
      window.location.href = '/home';
    })
    .catch(err => console.log(err));
  }

  const handleLogin = async() => {
    const loginCredentials = {
        mail: loginMail.current.value,
        password: loginPassword.current.value
    }
    await axios.post("http://localhost:8000/login", loginCredentials)
    .then(res => console.log(res.data))
    .catch(err => console.log(err));
  }

  return (
    <div>
      <h1>Login</h1>
      <input type="text" placeholder='Email' ref={loginMail}/><br /><br />
      <input type="password" placeholder='Password' ref={loginPassword}/><br /><br />
      <button onClick={handleLogin}>Login</button>

      <h1>Register</h1>
      <input type="text" placeholder='Username' ref={registerUsername}/><br /><br />
      <input type="text" placeholder='Email' ref={registerMail}/><br /><br />
      <input type="password" placeholder='Password' ref={registerPassword}/><br /><br />
      <button onClick={handleRegister}>Register</button>
    </div>
  )
}

export default Login
