import axios from 'axios';
import jwtDecode from 'jwt-decode';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const EditPost = () => {

    const {id} = useParams();
    const [post, setPost] = useState([]);
    const [user, setUser] = useState();
    const [files, setFiles] = useState([]);
    const [img, setImg] = useState([]);
    const [imageLink, setImageLink] = useState([]);
    const [updatedImg, setUpdatedImg] = useState([]);
    // const [isChecked, setChecked] = useState([]);
    
    const address = React.createRef();
    const mobile = React.createRef();
    const rent = React.createRef();
    const description = React.createRef();

    useEffect(() => {
        const fetchPost= async() => {
        console.log("Post id: ", id);
        const token = localStorage.getItem("secretToken");
        console.log(token);
        if(token)
        {
            const user = jwtDecode(token);
            console.log("user id: ", user.currentUser);
            setUser(user.currentUser);
        }
        else{
            window.location.href = '/home';
        }
        await axios.get(`http://localhost:8000/specific-post/${id}`)
        .then(res => {
            setPost(res.data);
            setImg(res.data.imgUrl);
            console.log(res.data);
            setImageLink([...res.data.imgUrl]);
        }
        )
        .catch(err => console.log(err));
    }
    fetchPost()
},[])
    const onFileUpload = (e) => {
        setFiles(e.target.files);
    }

    const editPost = async(e) => {
        e.preventDefault();
        console.log("Clicked");
        updatedImg.length = 0;
        const imgs = document.getElementsByName("deleteImg");
        for(let i=0;i<img.length;i++)
        {
            console.log(imgs[i].checked);
            if(imgs[i].checked !== true)
            {
                console.log(img[i]);
                updatedImg.push(img[i]);
            }
        }
        console.log(updatedImg);
        const formData = new FormData();
        for(let i=0;i<files.length;i++)
        {
            formData.append("image", files[i]);
        }
        const config = {
            headers: {
                'content-type' : 'multipart/form-data'
            }
        }
        await axios.post("http://localhost:8000/generate-url", formData, config)
        .then(res => {
            console.log(res.data);
            for(let i=0;i<res.data.length;i++)
            {
                updatedImg.push({
                    filename: res.data[i].filename,
                    url: res.data[i].path
                });
                console.log(updatedImg);
            }
            const newPost = {
                author_id: user,
                address: address.current.value,
                mobile: mobile.current.value,
                rent: rent.current.value,
                description: description.current.value,
                imgUrl: updatedImg,
                // date: new Date()
            }
            console.log(newPost);
            
            // axios.post(`http://localhost:8000/edit-post/${id}`, newPost)
            // .then(res => {
            //         console.log(res.data);
            //         window.location.href = `/account/${user}`;
            //     })
            // .catch(err => console.log(err));
        })
        .catch(err => console.log(err));
    }

  return (
    <div>
      <h1>Edit Post</h1>
      <input type="text" value={post.address} ref={address}/><br /><br />
            <input type="text" value={post.mobile} placeholder='Mobile Number' ref={mobile}/><br /><br />
            <input type="text" value={post.rent} ref={rent}/><br /><br />
            <input type="text" value={post.description} ref={description}/><br /><br />
            <input type="file" name="image" id="" onChange={onFileUpload} multiple/><br /><br />
            {img.map((i) => (
                <div>
                <img src={i.url} alt="" width={300}/><br/>
                <input type="checkbox" name="deleteImg" id="" value={`'${i.filename}','${i.url}'`}/>
                <label htmlFor="">Delete</label>
                </div>
            ))}
      <button onClick={editPost}>Edit Post</button>
    </div>
  )
}

export default EditPost
