import React from 'react';
import axios from 'axios';
import { useState } from 'react';

const NewPost = () => {

    // const [file, setFile] = useState();

    // const onInputChange = (e) => {
    //     setFile(e.target.files[0]);
    // }

    const file = React.createRef();
    
    const handleNewPost = async(e) => {
        e.preventDefault();
        const image =  file.current.value;

        const formData = new FormData();
        formData.append(
          "image",
          image,
        //   this.state.selectedFile.name
        );
        
        console.log(formData);
        await axios.post("http://localhost:8000/new-post", formData)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));


    }
    return ( 
        <div>
            <h1>Add Post</h1>
            {/* <input type="text" placeholder='name' ref={name}/><br /><br />
            <input type="email" name="" id="" placeholder='email' ref={email}/><br /><br />
            <input type="password" placeholder='password' ref={password}/><br /><br /> */}
            <input type="file" name="image" id="" ref={file}/><br /><br />
            <button onClick={handleNewPost}>Add Post</button>
        </div>
     );
}
 
export default NewPost;
