import React from 'react';
import axios from 'axios';
import { useState } from 'react';

const NewPost = () => {

    const [files, setFiles] = useState([]);

    const onFileUpload = (e) => {
        setFiles(e.target.files);
    }
    
    const handleNewPost = async(e) => {
        e.preventDefault();
        const formData = new FormData();
        for(let i=0;i<files.length;i++)
        {
            formData.append("image", files[i]);
        }
        const config = {
            headers: {
                'content-type' : 'multipart/form-data'
            }
        }
        console.log(files);
        await axios.post("http://localhost:8000/new-post", formData, config)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));
    }
    
    return ( 
        <div>
            <h1>Add Post</h1>
            {/* <input type="text" placeholder='name' ref={name}/><br /><br />
            <input type="email" name="" id="" placeholder='email' ref={email}/><br /><br />
            <input type="password" placeholder='password' ref={password}/><br /><br /> */}
            <input type="file" name="image" id="" onChange={onFileUpload} multiple/><br /><br />
            <button onClick={handleNewPost}>Add Post</button>
        </div>
     );
}
 
export default NewPost;
