import React from 'react';
import axios from 'axios';
import { useState } from 'react';

const NewPost = () => {

    const [file, setFile] = useState();
  
    fileObj = [];
    fileArray = [];

    const onFileUpload = (e) => {
        fileObj.push(e.target.files);
        for (let i = 0; i < this.fileObj[0].length; i++) {
            this.fileArray.push(URL.createObjectURL(this.fileObj[0][i]))
        }
        setFile({ file: fileArray })
    }
    
    const handleNewPost = async(e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("image", file);
        const config = {
            headers: {
                'content-type' : 'multipart/form-data'
            }
        }
        await axios.post("http://localhost:8000/new-post", formData, config)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));
    }
    
    return ( 
        <div>
            <h1>Add Post</h1>
            {/* <input type="text" placeholder='name' ref={name}/><br /><br />
            <input type="email" name="" id="" placeholder='email' ref={email}/><br /><br />
            <input type="password" placeholder='password' ref={password}/><br /><br /> */}
            <input type="file" name="image" id="" onChange={onFileUpload}/><br /><br />
            <button onClick={handleNewPost}>Add Post</button>
        </div>
     );
}
 
export default NewPost;
