import axios from 'axios';
import React, { useEffect, useState } from 'react'
import jwtDecode from 'jwt-decode';
import { useParams } from 'react-router-dom';

const Account = () => {

  const {id} = useParams();
  const [posts, setPosts] = useState([]);
  const [user, setUser] = useState();

  useEffect(() => {
    const fetchPosts = async() => {
        // const token = sessionStorage.getItem("secretToken");
        //     console.log(token);
        //     if(token)
        //     {
        //         const user = jwtDecode(token);
        //         setUser(user.currentUser);
        //         await axios.get(`http://localhost:8000/mypost/${user.currentUser}`)
        //         .then(res => {
        //           setPosts(res.data);
        //           console.log(res.data);
        //         })
        //     }
        console.log(id);
            if(id)
            {
              await axios.get(`http://localhost:8000/mypost/${id}`)
              .then(res => {
                setPosts(res.data);
                console.log(res.data);
              })
            }
            else{
              window.location.href = '/home';
            }
    }
    fetchPosts();
  },[])

  const deletePost = async(id) => {
    await axios.delete(`http://localhost:8000/delete-post/${id}`)
    .then(res => window.location.href = `/account/${user}`)
    .catch(err => console.log(err))
  }

  return (
    <div>
      <h1>Account</h1>
      <a href={`/account/${user}/new-post`}>Add Post</a>
      <h1>My Post</h1>
      {posts.map(p => (
        <div>
         {p.imgUrl.map(i => (
          <img src={i.url} alt="" width={300}/>
        ))}
        <p>{p.address}</p>
        <p>{p.mobile}</p>
        <p>{p.rent}</p>
        <a href={`/account/${p._id}/edit-post`}><button>Edit</button></a><br /><br />
        <button onClick={() => deletePost(p._id)}>Delete</button>
        </div>
      ))}
    </div>
  )
}

export default Account;
