import React, { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import jwtDecode from 'jwt-decode';

const NewPost = () => {
    const address = React.createRef();
    const mobile = React.createRef();
    const rent = React.createRef();
    const description = React.createRef();

    const[user, setUser] = useState();
    const [files, setFiles] = useState([]);
    const [imageLink, setImageLink] = useState([]);
    const [customError, setCustomError] = useState();

    useEffect(() => {
        const fetchToken = () => {
            const token = localStorage.getItem("secretToken");
            console.log(token);
            if(token)
            {
                const user = jwtDecode(token);
                console.log(user.currentUser);
                setUser(user.currentUser);
            }
            else{
                window.location.href = '/home';
            }
        }
        fetchToken();
    },[])

    const onFileUpload = (e) => {
        setFiles(e.target.files);
    }
    
    const addPost = async(e) => {
        e.preventDefault();
        console.log("Clicked");
        if(files.length > 0){
            const formData = new FormData();
            for(let i=0;i<files.length;i++)
            {
                formData.append("image", files[i]);
            }
            const config = {
                headers: {
                    'content-type' : 'multipart/form-data'
                }
            }
            // await axios.post("http://localhost:8000/generate-url", formData, config)
            // .then(res => {
            //     console.log(res.data);
            //     imageLink.length = 0;
            //     for(let i=0;i<res.data.length;i++)
            //     {
            //         imageLink.push({
            //             filename: res.data[i].filename,
            //             url: res.data[i].path
            //         });
            //     }
                const newPost = {
                    author_id: user,
                    address: address.current.value,
                    mobile: mobile.current.value,
                    rent: rent.current.value,
                    description: description.current.value,
                    imgUrl: imageLink,
                    date: new Date()
                }
                console.log(newPost);
            
                axios.post("http://localhost:8000/new-post", newPost)
                .then(res => {
                        if(typeof(res.data) == 'object')
                        {
                            console.log(res.data);
                            setCustomError(res.data.error);
                        }
                        else{
                        // window.location.href = `/account/${user}`;
                        }
                    })
                .catch(err => console.log(err));
            // })
            // .catch(err => console.log(err));
        }
        else{
            console.log('Atleast 1 Image is needed');
        }
    }
    
    return ( 
    
        (user? 
        <div>
            <h1>Add Post</h1>
            <input type="file" name="image" id="" onChange={onFileUpload} multiple/><br /><br />
            <input type="text" placeholder='Address' ref={address}/><br /><br />
            <input type="text" name="" id="" placeholder='Mobile Number' ref={mobile}/><br /><br />
            <input type="text" placeholder='$Rent' ref={rent}/><br /><br />
            <input type="text" placeholder='Description' ref={description}/><br /><br />
            <p>{customError}</p>
            <button onClick={addPost}>Add Post</button>
            {/* {imageLink.map(img => (
                <img src={img.url} alt="" />
            ))} */}
        </div> :
        <h1>Please login first..</h1>
        ) 
     );
}
 
export default NewPost;
