import React, { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import jwtDecode from 'jwt-decode';

const NewPost = () => {
    const address = React.createRef();
    const mobile = React.createRef();
    const rent = React.createRef();
    const description = React.createRef();

    const[user, setUser] = useState();
    const [files, setFiles] = useState([]);
    const [imageLink, setImageLink] = useState([]);
    const [customError, setCustomError] = useState();

    useEffect(() => {
        const fetchToken = () => {
            const token = sessionStorage.getItem("secretToken");
            console.log(token);
            if(token)
            {
                const user = jwtDecode(token);
                console.log(user.currentUser);
                setUser(user.currentUser);
            }
            else{
                window.location.href = '/home';
            }
        }
        fetchToken();
    },[])

    const onFileUpload = (e) => {
        setFiles(e.target.files);
    }

    const validation = () => {
        const mobilePattern = '[6789][0-9]{9}';
        const rentPattern = '\d{1,10}';
        if(!address.current.value)
        {
            setCustomError("Address is required");
            return false;
        }
        else if(address.current.value.length<10)
        {
            setCustomError("Address should be more than 10 characters");
        }
        else if(!mobile.current.value)
        {
            setCustomError("Mobile Number is required");
        }
        else if(!mobile.current.value.match(mobilePattern))
        {
            setCustomError("Mobile Number should be 10 digits");
        }
        else if(!rent.current.value)
        {
            setCustomError("Rent is required");
        }
        else if(!rent.current.value.match(rentPattern))
        {
            setCustomError("Rent should not be negative");
        }
        else if(!description.current.value)
        {
            setCustomError("Description is Required");
        }
    }
    
    const addPost = async(e) => {
        e.preventDefault();
        console.log("Clicked");
        let isValidated = validation();
        if(isValidated){
        if(files.length > 0){
            const formData = new FormData();
            for(let i=0;i<files.length;i++)
            {
                formData.append("image", files[i]);
            }
            const config = {
                headers: {
                    'content-type' : 'multipart/form-data'
                }
            }
            // TODO: generate url after the validation
            await axios.post("http://localhost:8000/generate-url", formData, config)
            .then(res => {
                console.log(res.data);
                imageLink.length = 0;
                for(let i=0;i<res.data.length;i++)
                {
                    imageLink.push({
                        filename: res.data[i].filename,
                        url: res.data[i].path
                    });
                }
                const newPost = {
                    author_id: user,
                    address: address.current.value,
                    mobile: mobile.current.value,
                    rent: rent.current.value,
                    description: description.current.value,
                    imgUrl: imageLink,
                    date: new Date()
                }
                console.log(newPost);
            
                axios.post("http://localhost:8000/new-post", newPost)
                .then(res => {
                        if(typeof(res.data) == 'object')
                        {
                            console.log(res.data);
                            setCustomError(res.data.error);
                        }
                        else{
                            window.location.href = `/account/${user}`;
                        }
                    })
                .catch(err => console.log(err));
            })
            .catch(err => console.log(err));
        }
        else{
            console.log('Atleast 1 Image is needed');
        }
        }
    }
    
    return ( 
    
        (user? 
        <div>
            <h1>Add Post</h1>
            <form action="" onSubmit={addPost}>
            <input type="file" name="image" id="" onChange={onFileUpload} multiple required/><br /><br />
            <input type="text" placeholder='Address' ref={address} /><br /><br />
            <input type="text" name="" id="" placeholder='Mobile Number' ref={mobile} /><br /><br />
            <input type="text" placeholder='$Rent' ref={rent} /><br /><br />
            <select name="" id="" ref={description}>
                <option value="1 Bedroom">1 Bed Room</option>
                <option value="2 Bedroom">2 Bed Room</option>
                <option value="3 Bedroom">3 Bed Room</option>
                <option value="4 Bedroom">4 Bed Room</option>
            </select>
            {/* <input type="text" placeholder='Description' ref={description} /><br /><br /> */}

            {customError?
            <p style={{color: "red"}}>{customError}</p>:''}
            <input type="submit" value="Add Post" />
            {/* {imageLink.map(img => (
                <img src={img.url} alt="" />
            ))} */}
            </form>
        </div> :
        <h1>Please login first..</h1>
        ) 
     );
}
 
export default NewPost;
