import axios from 'axios';
import jwtDecode from 'jwt-decode';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const EditPost = () => {

    const {id} = useParams();
    const [post, setPost] = useState([]);
    const [user, setUser] = useState();

    useEffect(() => {
        const fetchPost= async() => {
        console.log("Post id: ", id);
        const token = localStorage.getItem("secretToken");
        console.log(token);
        if(token)
        {
            const user = jwtDecode(token);
            console.log("user id: ", user.currentUser);
            setUser(user.currentUser);
        }
        await axios.get(`http://localhost:8000/specific-post/${id}`)
        .then(res => {
            setPost(res.data);
            console.log(res.data);
        }
        )
        .catch(err => console.log(err));
    }
    fetchPost()
    },[])

    const add = document.getElementById("add").value;
    console.log(add);
  return (
    <div>
      <h1>Edit Post</h1>
      <input type="text" value={post.address} id='add'/><br /><br />
      <input type="text" value={post.rent}/><br /><br />
      <input type="text" value={post.description}/><br /><br />
    </div>
  )
}

export default EditPost
