import React from 'react';
import axios from 'axios';
import { useState } from 'react';

const NewPost = () => {

    const address = React.createRef();
    const mobile = React.createRef();
    const rent = React.createRef();
    const description = React.createRef();

    const [files, setFiles] = useState([]);

    const onFileUpload = (e) => {
        setFiles(e.target.files);
    }
    
    const handleNewPost = async(e) => {
        e.preventDefault();
        const formData = new FormData();
        for(let i=0;i<files.length;i++)
        {
            formData.append("image", files[i]);
        }
        formData.append("post",{
            address: address.current.value,
            mobile: mobile.current.value,
            rent: rent.current.value,
            description: description.current.value,
        })
        const config = {
            headers: {
                'content-type' : 'multipart/form-data'
            }
        }

        // const newPost = {
        //     address: address.current.value,
        //     mobile: mobile.current.value,
        //     rent: rent.current.value,
        //     description: description.current.value,
        //     file: formData , config
        // }

        console.log(formData.get(image));
        await axios.post("http://localhost:8000/new-post", formData, config)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));
    }
    
    return ( 
        <div>
            <h1>Add Post</h1>
            <input type="text" placeholder='Address' ref={address}/><br /><br />
            <input type="text" name="" id="" placeholder='Mobile Number' ref={mobile}/><br /><br />
            <input type="text" placeholder='$Rent' ref={rent}/><br /><br />
            <input type="text" placeholder='Description' ref={description}/><br /><br />
            <input type="file" name="image" id="" onChange={onFileUpload} multiple/><br /><br />
            <button onClick={handleNewPost}>Add Post</button>
        </div>
     );
}
 
export default NewPost;
