import React, { useEffect, useState } from 'react';
import jwtDecode from 'jwt-decode';
import { NavLink } from 'react-router-dom';
 
const Navbar = () => {

    const [user, setUser] = useState();

    useEffect(() => {
        const fetchToken = () => {
            const token = localStorage.getItem("secretToken");
            console.log(token);
            if(token)
            {
                const user = jwtDecode(token);
                setUser(user.currentUser);
            }
        }
        fetchToken();
    },[])
    return ( 
        <div>
        <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
        <div class="container-fluid">
            <a class="navbar-brand" href="#">Navbar</a>
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav">
                <li class="nav-item">
                <NavLink class="nav-link" aria-current="page" href="/home">Home</NavLink>
                </li>
                {user ?
                <li class="nav-item">
                <NavLink class="nav-link" href={`/account/${user}`}>My Account</NavLink>
                </li> :
                <li class="nav-item">
                <NavLink class="nav-link" href="/login">Login</NavLink>
                </li>}
            </ul>
            </div>
        </div>
        </nav>
        {/* <div>
            <ul>
                <li><a href="/">Home</a></li>
                { user? 
                <li><a href={`/account/${user}`}>My Account</a></li> :
                <li><a href="/login">Login</a></li> }
            </ul>
        </div> */}
        </div>
     );
}
 
export default Navbar;
