import axios from 'axios';
import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';

const Home = () => {
    const [posts, setPosts] = useState([]);
    const [sortBy, setSortBy] = useState('Address');
    
    useEffect(() =>{
        async function fetchPost(){
            await axios.get("http://localhost:8000/allpost")
            .then(res => {
                setPosts(res.data);
                console.log(res.data);
            })
            .catch(err => console.log(err));
        }
        fetchPost();
    },[])

    const handleSearchBy = async(e) => {
        setSortBy(e.target.value);
        console.log(e.target.value);
    }
    const searchKey = React.createRef();
    const handleSearch = async() =>{
        // const searchBy = {
        //     searchKey: searchKey.current.value,
        //     sortBy
        // }
        const key = searchKey.current.value;
        // console.log(searchBy);
        await axios.get(`http://localhost:8000/search?searchKey=${key}&sortBy=${sortBy}`)
        .then(res => setPosts(res.data))
        .catch(err => console.log(err));
    }

    const openPost =async() =>{

    }
    return ( 
        <div>
            <select onChange={handleSearchBy}>
                <option value="Address">Address</option>
                <option value="Rent">Rent</option>
                <option value="Rooms">Rooms</option>
            </select>
            <input type="text" placeholder='Search' ref={searchKey}/>
            <button onClick={handleSearch}>Search</button>
            <h1>All Post</h1>
            {posts.map(p => (
                <div onClick={() => {window.location.href = `/home/:${p._id}`}}>
                    <img src={p.imgUrl} alt="" width={300}/>
                    <p>{p.address}</p>
                    <p>{p.mobile}</p>
                    <p>{p.rent}</p>
                    </div>
            ))}
        </div>
     );
}
 
export default Home;
