import axios from 'axios';
import React, { useEffect, useState } from 'react'
import jwtDecode from 'jwt-decode';
import { useParams } from 'react-router-dom';

const Account = () => {

  const {id} = useParams();
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async() => {
            if(id)
            {
              await axios.get(`http://localhost:8000/mypost/${id}`)
              .then(res => {
                setPosts(res.data);
                console.log(res.data);
              })
            }
            else{
              window.location.href = '/post';
            }
    }
    fetchPosts();
  },[])

  const deletePost = async(postId) => {
    await axios.delete(`http://localhost:8000/delete-post/${postId}`)
    .then(res => window.location.href = `/account/${id}`)
    .catch(err => console.log(err))
  }

  return (
    <div>
      <h1>Account</h1>
      <a href={`/account/${id}/new-post`}>Add Post</a>
      <h1>My Post</h1>
      {posts.length? 
      posts.map(p => (
        <div>
         {p.imgUrl.map(i => (
          <img src={i.url} alt="" width={300}/>
        ))}
        <p>{p.address}</p>
        <p>{p.mobile}</p>
        <p>{p.rent}</p>
        <a href={`/account/${p._id}/edit-post`}><button>Edit</button></a><br /><br />
        <button onClick={() => deletePost(p._id)}>Delete</button>
        </div>
      )):
      <h3>No Post Added</h3> }
    </div>
  )
}

export default Account;
