import axios from 'axios';
import React, { useEffect, useState } from 'react'
import jwtDecode from 'jwt-decode';
import { useParams } from 'react-router-dom';

const Account = () => {

  const {id} = useParams();
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async() => {
            if(id)
            {
              await axios.get(`http://localhost:8000/mypost/${id}`)
              .then(res => {
                setPosts(res.data);
                console.log(res.data);
              })
            }
            else{
              window.location.href = '/home';
            }
    }
    fetchPosts();
  },[])

  const deletePost = async(postId) => {
    await axios.delete(`http://localhost:8000/delete-post/${postId}`)
    .then(res => window.location.href = `/account/${id}`)
    .catch(err => console.log(err))
  }

  return (
    <div>
      <h1>Account</h1>
      <a href={`/account/${id}/new-post`}>Add Post</a>
      <div className="container-fluid post-details">
      <div className="row">
        <div className="col-7">
          <Carousel>
          {img.map(i => (
                <Carousel.Item className='carousel-item'>
                  <img src={i.url} alt="..." className='carousel-img' />
                </Carousel.Item>
              ))}
          </Carousel>
        </div>
        <div className='col-5 details'> 
            <h1>Details</h1>
            <p><span>Location <FaHome/></span> : {post.address}</p>
            <p><span>Contact Number <FaMobile/></span> : {post.mobile}</p>
            <p><span>Rent <FaRupeeSign/></span> : {post.rent}</p>
            <p><span>Bed Rooms <FaBed/></span> : {post.description}</p>
            {post.author_id === user? 
              <div>
                <button onClick={() => {window.location.href = `/account/${id}/edit-post`}}>Edit</button><br />
                <button onClick={() => deletePost(post._id)}>Delete</button>
              </div>: ''}
        </div>
      </div>
    </div>
      <h1>My Post</h1>
      {posts.length? 
      posts.map(p => (
        <div>
         {p.imgUrl.map(i => (
          <img src={i.url} alt="" width={300}/>
        ))}
        <p>{p.address}</p>
        <p>{p.mobile}</p>
        <p>{p.rent}</p>
        <a href={`/account/${p._id}/edit-post`}><button>Edit</button></a><br /><br />
        <button onClick={() => deletePost(p._id)}>Delete</button>
        </div>
      )):
      <h3>No Post Added</h3> }
    </div>
  )
}

export default Account;
