        //         updatedImg.push({
        //             filename: res.data[i].filename,
        //             url: res.data[i].path
        //         });
        //         console.log(updatedImg);
        //     }
        //     const newPost = {
        //         author_id: user,
        //         address: address.current.value,
        //         mobile: mobile.current.value,
        //         rent: rent.current.value,
        //         description: description.current.value,
        //         imgUrl: updatedImg,
        //         // date: new Date()
        //     }




        //     console.log(newPost);
            
        //     axios.post(`http://localhost:8000/edit-post/${id}`, newPost)
        //     .then(res => {
        //             console.log(res.data);
        //             window.location.href = `/account/${user}`;
        //         })
        //     .catch(err => console.log(err));
        // })
        // .catch(err => console.log(err));
    }

  return (
    <div>
      <h1>Edit Post</h1>
        <input type="text" defaultValue={post.address} ref={address}/><br /><br />
        <input type="text" defaultValue={post.mobile} placeholder='Mobile Number' ref={mobile}/><br /><br />
        <input type="text" defaultValue={post.rent} ref={rent}/><br /><br />
        <input type="text" defaultValue={post.description} ref={description}/><br /><br />
        <input type="file" name="image" id="" onChange={onFileUpload} multiple/><br /><br />
            {img.map((i) => (
                <div>
                <img src={i.url} alt="" width={300}/><br/>
                <input type="checkbox" name="deleteImg" id=""/>
                <label htmlFor="">Delete</label>
                </div>
            ))}
      <button onClick={editPost}>Edit Post</button>
    </div>
  )
}

export default EditPost
