import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import jwtDecode from 'jwt-decode';
import '../css/Post.css';
import sampleimg from '../images/jacques-bopp-Hh18POSx5qk-unsplash.jpg';

const Post = () => {
  const { id } = useParams();
  const [post, setPost] = useState([]);
  const [user, setUser] = useState();
  const [img, setImg] = useState([]);

  useEffect(() => {
    const fetchPost= async() => {
    console.log("Post id: ", id);
    const token = sessionStorage.getItem("secretToken");
    console.log(token);
    if(token)
    {
        const user = jwtDecode(token);
        console.log("user id: ", user.currentUser);
        setUser(user.currentUser);
    }
    await axios.get(`http://localhost:8000/specific-post/${id}`)
    .then(res => {
        setPost(res.data);
        setImg(res.data.imgUrl)
        console.log(res.data);
    }
    )
    .catch(err => console.log(err));
}
fetchPost()
},[])

const deletePost = async(id) => {
  await axios.delete(`http://localhost:8000/delete-post/${id}`)
  .then(res => window.location.href = `/account/${user}`)
  .catch(err => console.log(err))
}

  return (
    <div className="container-fluid post-details">
      <div className="row">
        <div className="col-8">
          <div id="carouselExampleIndicators" class="carousel slide" data-bs-ride="carousel">
            <div class="carousel-inner">
              {img.forEach((i, index) => (
                <div class="carousel-item ">
                  <img src={i.url} class="d-block w-100" alt="..."/>
                </div>
              ))}
              {/* <div class="carousel-item">
                <img src={sampleimg} class="d-block w-100" alt="..."/>
              </div> */}
            </div>
            <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="prev">
              <span class="carousel-control-prev-icon" aria-hidden="true"></span>
              <span class="visually-hidden">Previous</span>
            </button>
            <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="next">
              <span class="carousel-control-next-icon" aria-hidden="true"></span>
              <span class="visually-hidden">Next</span>
            </button>
          </div>
        </div>
        <div className='col-4 details'> 
            <h1>Post</h1>
            {/* {img.map(i => (
            <img src={i.url} alt="" width={300}/>
            ))} */}
            <p>{post.address}</p>
            <p>{post.mobile}</p>
            <p>{post.rent}</p>
            {post.author_id === user? 
              <div>
              <a href={`/account/${id}/edit-post`}><button>Edit</button></a><br /><br />
              <button onClick={() => deletePost(post._id)}>Delete</button>
              </div>: ''}
        </div>
      </div>
    </div>
  )
}

export default Post
