import axios from 'axios';
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const Login = () => {

  const [userCredentials, setUserCredentials] = useState([]);
  const history = useHistory();
  const registerUsername = React.createRef();
  const registerMail = React.createRef();
  const registerPassword = React.createRef();
  const checkCode = React.createRef();

  const loginMail = React.createRef();
  const loginPassword = React.createRef();

  const handleRegister = async() => {
    const registerCredentials = {
        email_id: registerMail.current.value,
        username: registerUsername.current.value,
        password: registerPassword.current.value
    }

    await axios.post("http://localhost:8000/auth/validateRegister", registerCredentials)
    .then(res => {
      console.log(res.data);
      setUserCredentials(res.data);
      history.push('/check-pin', {...userCredentials});
    })
    .catch(err => console.log(err));

    // await axios.post("http://localhost:8000/auth/register", registerCredentials)
    // .then(res => {
    //   console.log(res.data);
    //   const jwt = res.data.token;
    //   localStorage.setItem("secretToken", jwt);
    //   window.location.href = '/home';
    // })
    // .catch(err => console.log(err));
  }

  const handleLogin = async() => {
    const loginCredentials = {
        email_id: loginMail.current.value,
        password: loginPassword.current.value
    }
    await axios.post("http://localhost:8000/auth/login", loginCredentials)
    .then(res => {
      console.log(res.data);
      const jwt = res.data.token;
      if(jwt !== undefined)
      {
        localStorage.setItem("secretToken", jwt);
        window.location.href = '/home';
      }
      else{
        console.log(res.data);
      }
    })
    .catch(err => console.log(err));
  }

  return (
    <div>
      <h1>Login</h1>
      <input type="text" placeholder='Email' ref={loginMail}/><br /><br />
      <input type="password" placeholder='Password' ref={loginPassword}/><br /><br />
      <button onClick={handleLogin}>Login</button>

      <h1>Register</h1>
      <input type="text" placeholder='Email' ref={registerMail} value='margot@gmail.com' required/><br /><br />
      <input type="text" placeholder='Username' ref={registerUsername} value='Margot Robbie' required/><br /><br />
      <input type="password" placeholder='Password' ref={registerPassword} value='Margot@143' required/><br /><br />
      <button onClick={handleRegister}>Register</button><br /><br />
    </div>
  )
}

export default Login;
