import axios from 'axios';
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const CheckPin = () => {
  const {state} = useLocation();
  console.log(state);
  const [userCredentials, setUserCredentials] = useState(state);

  const checkCode = React.createRef();

  const handleRegister = async() => {
    await axios.post("http://localhost:8000/auth/register", userCredentials)
    .then(res => {
      console.log(res.data);
      const jwt = res.data.token;
      localStorage.setItem("secretToken", jwt);
      window.location.href = '/home';
    })
    .catch(err => console.log(err));
  }
  return (
    <div>
      <h1>{userCredentials.sixDigitCode}</h1>
      <input type="text" placeholder='6 Digit Code' ref={checkCode} required/><br /><br />
      <button onClick={handleRegister}>Confirm</button>
    </div>
  )
}

export default CheckPin
