import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import jwtDecode from 'jwt-decode';

const Post = () => {
  const { id } = useParams();
  const [post, setPost] = useState([]);
  const [user, setUser] = useState();

  useEffect(() => {
    const fetchPost= async() => {
    console.log("Post id: ", id);
    const token = localStorage.getItem("secretToken");
    console.log(token);
    if(token)
    {
        const user = jwtDecode(token);
        console.log("user id: ", user.currentUser);
        setUser(user.currentUser);
    }
    await axios.get(`http://localhost:8000/specific-post/${id}`)
    .then(res => {
        setPost(res.data);
        console.log(res.data);
    }
    )
    .catch(err => console.log(err));
}
fetchPost()
},[])

  return (
    <div> 
        <h1>Post</h1>
        {post.map(p => (
                  <div>
                    {p.imgUrl.map(i => (
                        <img src={i} alt="" width={300}/>
                    ))}
                    <p>{p.address}</p>
                    <p>{p.mobile}</p>
                    <p>{p.rent}</p>
                    {p.author_id === user? 
                  <div>
                  <a href={`/home/${id}/edit-post`}><button>Edit</button></a><br /><br />
                  <button>Delete</button>
                  </div>: ''}
                  </div>
            ))}


        {/* {post.imgUrl.map(i => (
        //   <img src={i} alt="" width={300}/>
        // ))} */}
        {/* <p>{post.address}</p>
        <p>{post.mobile}</p>
        <p>{post.rent}</p>
        {post.author_id === user? 
          <div>
          <a href={`/home/${id}/edit-post`}><button>Edit</button></a><br /><br />
          <button>Delete</button>
          </div>: ''} */}
    </div>
  )
}

export default Post
