import React from 'react';
import axios from 'axios';

const NewPost = () => {

    const image = React.createRef();
    
    const handleNewPost = async(e) => {
        e.preventDefault();
        const file =  image.current.value;

        const formData = new FormData();
        formData.append(
          "myFile",
          file,
        //   this.state.selectedFile.name
        );
        
        console.log(file);
        await axios.post("http://localhost:8000/new-post", file)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));


    }
    return ( 
        <div>
            <h1>Add Post</h1>
            {/* <input type="text" placeholder='name' ref={name}/><br /><br />
            <input type="email" name="" id="" placeholder='email' ref={email}/><br /><br />
            <input type="password" placeholder='password' ref={password}/><br /><br /> */}
            <input type="file" name="image" id="" ref={image}/><br /><br />
            <button onClick={handleNewPost}>Add Post</button>
        </div>
     );
}
 
export default NewPost;
